import React from "react";
import axios from "axios";

class Transaksi extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            user_id: "",
            status: "",
            metodebayar: "",
            kurir: ""
        };
    }

    addTrx = async event => {
        event.preventDefault();
        const self = this;
        const setting = {
            method: "post",
            url: self.props.baseUrl + "/carts/checkout",
            data: {
                user_id: localStorage.getItem("user_id"),
                status: self.setState({ status: "PENDING" }),
                metodebayar: self.state.metodebayar,
                kurir: self.state.kurir
            },
            headers: {
                Authorization: "Bearer " + localStorage.getItem("token")
            }
        };
        await axios(setting)
            .then(function(response) {
                console.log("add cart ok", response.data);
                self.props.history.replace(
                    "/carts/" + localStorage.getItem("user_id")
                );
            })
            .catch(function(error) {
                console.log("error ni", error);
                alert("Silahkan klik Log In untuk melanjutkan transaksi anda!");
            });
    };
    render() {
        return (
            <section className="content signin d-flex justify-content-center">
                <form
                    style={{
                        height: "310px",
                        width: 400
                    }}
                    className="border p-5 align-self-center text-center"
                >
                    <h4>Form Pengiriman Barang</h4>
                    <div>
                        <input
                            style={{
                                width: "100%"
                            }}
                            type="text"
                            name="transfer"
                            placeholder="Transfer"
                            onChange={e => {
                                e.preventDefault();
                                this.setState({
                                    metodebayar: e.target.value
                                });
                            }}
                        />
                    </div>
                    <div>
                        <input
                            style={{
                                width: "100%"
                            }}
                            type="text"
                            name="kurir"
                            placeholder="JNT"
                            onChange={e => {
                                e.preventDefault();
                                this.setState({
                                    kurir: e.target.value
                                });
                            }}
                        />
                    </div>
                    <button
                        className="btn btn-secondary"
                        onClick={e => this.InputBarang(e)}
                    >
                        SignUp
                    </button>
                </form>
            </section>
        );
    }
}
export default Transaksi;
