import React from "react";
import NavBarPos from "../component/NavbarAfterLogin";
import { connect } from "unistore/react";
import { actions } from "../store";
import axios from "axios";
import "../Aset/css/header.css";
import Picturedef from "../Aset/grain.png";

class ItemsDetail extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            urlfoto: "",
            jumlah: "",
            username: "",
            barang_id: "",
            name: "",
            category: "",
            deskripsi: ""
        };
    }
    componentDidMount() {
        const self = this;
        const set = {
            method: "GET",
            url:
                self.props.baseUrl +
                "/items/list/" +
                self.props.match.params.item_id,
            headers: {
                Authorization: "Bearer " + self.props.token
            }
        };
        axios(set)
            .then(function(response) {
                console.log("detail ok", response.data);
                self.setState({ urlfoto: response.data.urlfoto });
                self.setState({ jumlah: response.data.stok });
            })
            .catch(function(error) {
                console.log("error ni", error);
            });
    }

    addCart = event => {
        event.preventDefault();
        const self = this;
        console.log("addcart");
        const setting = {
            method: "PATCH",
            url:
                self.props.baseUrl +
                "/items/list/" +
                self.props.match.params.item_id,
            data: {
                user_id: self.props.user_id
            },
            headers: {
                Authorization: "Bearer " + self.props.token
            }
        };
        axios(setting)
            .then(function(response) {
                console.log("add cart ok", response.data);
                self.props.history.replace("/items/list");
            })
            .catch(function(error) {
                console.log("error ni", error);
                alert("Silahkan klik Log In untuk melanjutkan transaksi anda!");
            });
    };
    render() {
        return (
            <div className="container-fluid">
                <NavBarPos />
                <div className="container">
                    <div className="row justify-content-center border mx-1 my-4 p-4">
                        <div className="col-6 col-md-12">
                            <div className="row justify-content-center">
                                <img
                                    className="w-100"
                                    src={
                                        this.state.itemlist.urlfoto === null
                                            ? Picturedef
                                            : this.state.itemlist.urlfoto
                                    }
                                    alt=""
                                />
                            </div>
                            <div className="row justify-content-center">
                                <h5>
                                    {this.state.itemlist.jumlah > 0
                                        ? this.state.itemlist.jumlah
                                        : "OUT OF STOCK"}
                                </h5>
                            </div>
                        </div>
                        <div className="col-6 col-md-12">
                            <div className="row justify-content-center">
                                <h2 className="col-12">
                                    {this.state.itemlist.username.toUpperCase()}
                                </h2>
                                <h2 className="col-12">
                                    barang_id:{this.state.itemlist.id}
                                </h2>
                                <h2 className="col-12">
                                    {this.state.itemlist.name}
                                </h2>
                                <h2 className="col-12">
                                    {this.state.itemlist.harga}
                                </h2>
                                <h4 className="col-12">
                                    {this.state.itemlist.category}
                                </h4>
                                <p className="col-12">
                                    {this.state.itemlist.deskripsi}
                                </p>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}
export default connect(
    "baseUrl",
    actions
)(ItemsDetail);
