import React from "react";
import NavBarPos from "../component/NavbarAfterLogin";
import { Link } from "react-router-dom";
import { connect } from "unistore/react";
import { actions } from "../store";
import axios from "axios";
import "../Aset/css/header.css";

class ItemsDetail extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            urlfoto: "",
            name: "",
            stok: "",
            harga: ""
        };
    }
    componentDidMount() {
        const self = this;
        const set = {
            method: "get",
            url: self.props.baseUrl + self.props.match.params.item_id
        };
        axios(set)
            .then(function(response) {
                console.log("detail ok", response.data);
                self.setState({ urlfoto: response.data.urlfoto });
                self.setState({ name: response.data.user_id });
                self.setState(
                    { stok: response.data.stok },
                    console.log(response.data)
                );
                self.setState({ harga: response.data.harga });
            })
            .catch(function(error) {
                console.log("error ni", error);
            });
    }

    addCart = event => {
        event.preventDefault();
        _;
        const self = this;
    };
}
export default connect(
    "baseUrl",
    actions
)(ItemsDetail);
