import React from "react";
import NavBarPos from "../component/NavbarAfterLogin";
import { Link } from "react-router-dom";
import { connect } from "unistore/react";
import { actions } from "../store";
import axios from "axios";
import "../Aset/css/header.css";

class ItemsDetail extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            urlfoto: "",
            name: "",
            stok: "",
            harga: ""
        };
    }
    componentDidMount() {
        const self = this;
        const set = {
            method: "GET",
            url:
                self.props.baseUrl +
                "/items/list/" +
                self.props.match.params.item_id,
            headers: {
                Authorization: "Bearer " + self.props.token
            }
        };
        axios(set)
            .then(function(response) {
                console.log("detail ok", response.data);
                self.setState({ urlfoto: response.data.urlfoto });
                self.setState({ name: response.data.user_id });
                self.setState(
                    { stok: response.data.stok },
                    console.log(response.data)
                );
                self.setState({ harga: response.data.harga });
            })
            .catch(function(error) {
                console.log("error ni", error);
            });
    }

    addCart = event => {
        event.preventDefault();
        const self = this;
        console.log("addcart");
        const setting = {
            method: "PATCH",
            url:
                self.props.baseUrl +
                "/items/list/" +
                self.props.match.params.item_id,
            data: {
                user_id: self.props.user_id
            },
            headers: {
                Authorization: "Bearer " + self.props.token
            }
        };
        axios(set)
            .then(function(response) {
                console.log("detail ok", response.data);
                self.setState({ urlfoto: response.data.urlfoto });
                self.setState({ name: response.data.user_id });
                self.setState(
                    { stok: response.data.stok },
                    console.log(response.data)
                );
                self.setState({ harga: response.data.harga });
            })
            .catch(function(error) {
                console.log("error ni", error);
            });
    };
}
export default connect(
    "baseUrl",
    actions
)(ItemsDetail);
