import React from "react";
import swal from "sweetalert";
import { connect } from "unistore/react";
import { actions } from "../store";
import NavBarPos from "../component/NavbarAfterLogin";

class Profile extends React.Component {
    constructor(props) {
        super(props);
    }

    render() {
        if (this.props.status === 1) {
            swal({
                title: "Welcome Seller!",
                text: `${this.props.username} Yuk Tambah Barang jualanmu!`,
                icon: "success",
                button: "olrait!"
            });
        } else {
            swal({
                title: "Welcome Customer!",
                text: `${this.props.username} Yuk Dibeli dibeli!`,
                icon: "success",
                button: "olrait!"
            });
        }

        return (
            <div>
                <NavBarPos />
                <div className="contain">
                    <div className="row d-flex align-items-center homeground">
                        <div className="col-12 align-self-center text-center">
                            <img
                                src="https://image.flaticon.com/icons/svg/145/145846.svg"
                                alt=""
                                width="400px"
                                height="300px"
                            />
                            <div>
                                <h3>Username : {this.props.username} </h3>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}
export default connect(
    "username,user_id,status",
    actions
)(Profile);
