import React from "react";
import Picturedef from "../Aset/grain.png";

function ListItem(props) {
    return (
        <div className="row p-5">
            <img
                className="w-100"
                src={
                    props.items.urlfoto === null
                        ? Picturedef
                        : this.state.urlfoto
                }
                alt=""
            />
            <div
                className="row text-center   style={{
                        fontWeight: 700,
                        fontSize: 20
                    }}"
            >
                <p>barang id : {props.item.id}</p>

                {props.item.name}
                <span>
                    {this.state.jumlah > 0 ? this.state.jumlah : "OUT OF STOCK"}
                </span>
                <p>Rp {props.item.harga}</p>
            </div>
        </div>
    );
}
export default ListItem;
