import React from "react";
import NavBarPos from "../component/NavbarAfterLogin";
import { connect } from "unistore/react";
import { actions } from "../store";
import axios from "axios";
import Picturedef from "../Aset/grain.png";

class Carts extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            listCart: []
        };
    }

    showCart = async event => {
        event.preventDefault();
        const self = this;
        const setting = {
            method: "get",
            url:
                self.props.baseUrl +
                `/items/list/${localStorage.getItem("user_id")}`,
            headers: {
                Authorization: "Bearer " + localStorage.getItem("token")
            }
        };
        await axios(setting)
            .then(function(response) {
                console.log("show cart ok", response.data);
            })
            .catch(function(error) {
                console.log("error ni", error);
                alert("Silahkan klik Log In untuk melanjutkan transaksi anda!");
            });
    };
}
export default connect(
    "user_id",
    actions
)(Carts);
