import React from "react";
import NavBarPos from "../component/NavbarAfterLogin";
import { connect } from "unistore/react";
import { actions } from "../store";
import axios from "axios";
import "../Aset/css/header.css";

class ShowBarang extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            headline: []
        };
    }
    componentDidMount() {
        const self = this;
        axios
            .get(self.props.baseUrl + "/items/list")
            .then(function(response) {
                self.setState(
                    { headline: response.data },
                    console.log(self.state.headline)
                );
            })
            .catch(function(error) {
                console.log(error);
            });
    }

    render() {
        console.log(this.state.headline);
        let rapikan = this.state.headline.map((elm, key) => {
            return (
                <div>
                    <div
                        className="col-md-4"
                        style={{
                            marginTop: 5,
                            boxShadow: " 2px 2px 2px 2px #888888",
                            padding: 5,
                            backgroundColor: "white"
                        }}
                    >
                        <div className="col-md-4 text-center">
                            <img
                                src={elm.urlfoto}
                                alt=""
                                width="300px"
                                height="300px"
                            />
                            <div className="col-md-4">
                                <a style={{ fontWeight: 700, fontSize: 20 }}>
                                    {elm.name}
                                    <p>{elm.stok}</p>
                                    <p>{elm.harga}</p>
                                </a>
                            </div>
                        </div>
                    </div>
                </div>
            );
        });
        return (
            <div>
                <NavBarPos />
                <div className="container" style={{ marginTop: 20 }}>
                    <div className="row">
                        <div>{rapikan}</div>
                    </div>
                </div>
            </div>
        );
    }
}
export default connect(
    "baseUrl",
    actions
)(ShowBarang);
