import React from "react";
import ListCart from "../component/ListCart";
import NavBarPos from "../component/NavbarAfterLogin";
import { connect } from "unistore/react";
import { actions } from "../store";
import axios from "axios";
import { Button } from "react-bootstrap";

class Carts extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            listCart: [],
            status: "",
            metodebayar: "",
            kurir: ""
        };
    }

    componentDidMount = async () => {
        const self = this;
        const setting = {
            method: "get",
            url:
                self.props.baseUrl +
                `/carts/${self.props.match.params.cart_id}`,
            headers: {
                Authorization: "Bearer " + localStorage.getItem("token")
            }
        };
        await axios(setting)
            .then(function(response) {
                self.setState({ listCart: response.data });
                console.log("show cart ok", response.data);
            })
            .catch(function(error) {
                console.log("error ni", error);
                alert("Lakukan Pembelian Barang untuk Melihat Isi Cart!");
            });
    };
    addTrx = async event => {
        event.preventDefault();
        const self = this;
        const setting = {
            method: "post",
            url:
                self.props.baseUrl +
                `/items/list/${self.props.match.params.item_id}`,
            data: {
                user_id: localStorage.getItem("user_id")
            },
            headers: {
                Authorization: "Bearer " + localStorage.getItem("token")
            }
        };
        await axios(setting)
            .then(function(response) {
                console.log("add cart ok", response.data);
                self.props.history.replace(
                    "/carts/" + localStorage.getItem("user_id")
                );
            })
            .catch(function(error) {
                console.log("error ni", error);
                alert("Silahkan klik Log In untuk melanjutkan transaksi anda!");
            });
    };
    render() {
        return (
            <div>
                <NavBarPos />
                <div className="container" style={{ marginTop: 20 }}>
                    <div className="row">
                        {this.state.listCart.map((item, key) => {
                            return (
                                <div
                                    className="col-3"
                                    style={{
                                        boxShadow: " 2px 2px 2px 2px #888888",
                                        backgroundColor: "white"
                                    }}
                                >
                                    <ListCart item={item} />
                                </div>
                            );
                        })}
                    </div>
                    <button href=""></button>
                </div>
            </div>
        );
    }
}
export default connect(
    "baseUrl",
    actions
)(Carts);
