import { React } from "react";
import axios from "axios";

class Transaksi extends React.Component {
    addTrx = async event => {
        event.preventDefault();
        const self = this;
        const setting = {
            method: "post",
            url: self.props.baseUrl + "/carts/checkout",
            data: {
                user_id: localStorage.getItem("user_id")
            },
            headers: {
                Authorization: "Bearer " + localStorage.getItem("token")
            }
        };
        await axios(setting)
            .then(function(response) {
                console.log("add cart ok", response.data);
                self.props.history.replace(
                    "/carts/" + localStorage.getItem("user_id")
                );
            })
            .catch(function(error) {
                console.log("error ni", error);
                alert("Silahkan klik Log In untuk melanjutkan transaksi anda!");
            });
    };
    render() {
        return (
            <section className="content signin d-flex justify-content-center">
                <form
                    style={{
                        height: "310px",
                        width: 400
                    }}
                    className="border p-5 align-self-center text-center"
                >
                    <h4></h4>
                    <div>
                        <input
                            style={{
                                width: "100%"
                            }}
                            type="text"
                            name="name"
                            placeholder="Namabarang"
                            onChange={e => {
                                e.preventDefault();
                                this.setState({
                                    name: e.target.value
                                });
                            }}
                        />
                    </div>
                    <div>
                        <input
                            style={{
                                width: "100%"
                            }}
                            type="text"
                            name="stok"
                            placeholder="stok"
                            onChange={e => {
                                e.preventDefault();
                                this.setState({
                                    stok: e.target.value
                                });
                            }}
                        />
                    </div>
                    <div>
                        <input
                            style={{
                                width: "100%"
                            }}
                            type="text"
                            name="harga"
                            placeholder="harga"
                            onChange={e => {
                                e.preventDefault();
                                this.setState({
                                    harga: parseInt(e.target.value)
                                });
                            }}
                        />
                    </div>
                    <div>
                        <input
                            style={{
                                width: "100%"
                            }}
                            type="text"
                            name="category"
                            placeholder="category"
                            onChange={e => {
                                e.preventDefault();
                                this.setState({
                                    category: e.target.value
                                });
                            }}
                        />
                    </div>
                    <div>
                        <input
                            style={{
                                width: "100%"
                            }}
                            type="text"
                            name="urlfoto"
                            placeholder="urlfoto"
                            onChange={e => {
                                e.preventDefault();
                                this.setState({
                                    urlfoto: e.target.value
                                });
                            }}
                        />
                    </div>
                    <button
                        className="btn btn-secondary"
                        onClick={e => this.InputBarang(e)}
                    >
                        SignUp
                    </button>
                </form>
            </section>
        );
    }
}
export default Transaksi;
